export class Client {
    constructor();

    on(str: string, callback: (
        src: string, 
        dest: string, 
        namespace: string, 
        data: string
    ) => void): void;

    once(str: string, callback: (
        src: string, 
        dest: string, 
        namespace: string, 
        data: string
    ) => void): void;

    connect(
        options: (string | {
            port: number,
            rejectUnauthorized?: boolean,
            host: string
        }
        ),
        callback: () => void
    ): void;

    close(): void;

    send(
        sourceId: string,
        destinationId: string,
        namespace: string,
        data: string
    ): void;

    createChannel(
        sourceId: string,
        destinationId: string,
        namespace: string,
        encoding: string
    ): Channel;
}

export type Channel = {
    send(data: Record<string, unknown>): void;
    close(): void;
}